import React from "react";
import { createClient } from '@supabase/supabase-js';

const supabaseUrl = "https://jftaxymlbutkjoacvtbk.supabase.co";
const supabaseKey = "YOUR_SUPABASE_KEY"; // Replace with your Supabase key
const supabase = createClient(supabaseUrl, supabaseKey);

function Controls() {

    async function broadcasturl(e) {
        e.preventDefault();
        const nexturl = document.getElementById('url').value;

        // Set the current page in supabase
        const { error } = await supabase
            .from('controller')
            .update({ currentURL: nexturl })
            .eq('id', 1);

        if (error) {
            alert(error.message);
            return; // Stop execution if there's an error
        }

        // Broadcast the URL change on 'show' channel
        const channelB = supabase.channel('show');
        channelB.publish('urlchange', { url: nexturl });
    }

    return (
        <>
            <div style={{
                backgroundColor: "#1f1f1f",
                color: "white",
                height: "100vh",
                display: "flex",
                flexDirection: "column",
                width: "100vw",
                alignItems: "center",
                justifyContent: "center"
            }}>
                <h1>Controls</h1>
                <div style={{
                    backgroundColor: "#1f1f1f",
                    padding: "20px",
                    borderRadius: "10px",
                    border: "2px solid #424242",
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                    width: "fit-content",
                }}>
                    <h2 style={{
                        color: "white",
                    }}>URL Director</h2>
                    <input id="url" type="text" placeholder="URL" style={{
                        padding: "10px",
                        backgroundColor: "#1f1f1f",
                        color: "white",
                        borderRadius: "10px",
                        border: "2px solid #424242",
                        outline: "none",
                        fontSize: "20px",
                        marginBottom: "20px",
                        width: "300px"
                    }}></input>
                    <button onClick={broadcasturl} style={{
                        padding: "10px",
                        backgroundColor: "#1f1f1f",
                        color: "white",
                        borderRadius: "10px",
                        border: "none",
                        fontSize: "20px",
                        cursor: "pointer",
                        width: "200px"
                    }}>Broadcast</button>
                </div>
            </div>
        </>
    );
}

export default Controls;
