import React, { useState, useEffect } from 'react';
import { Route, Routes } from 'react-router-dom';
import { createClient } from '@supabase/supabase-js';
import Chat from './pages/Chat.jsx';
import Home from './pages/Home.jsx';
import Controls from './pages/Control.jsx';
import Vote from './pages/Vote.jsx';
import Board from './pages/Board.jsx';

const supabaseUrl = "https://jftaxymlbutkjoacvtbk.supabase.co";
const supabaseKey = "YOUR_SUPABASE_KEY";
const supabase = createClient(supabaseUrl, supabaseKey);

function App() {
  const [user, setUser] = useState(null);

  async function getUser() {
    try {
      const { data, error } = await supabase.auth.getUser();
      if (error) {
        setUser(null);
        throw error;
      }
      if (data.user) {
        setUser(data.user);
      } else {
        setUser(null);
      }
    } catch (error) {
      console.error('Error fetching user:', error.message);
    }
  }

  useEffect(() => {
    getUser();
  }, []);

  useEffect(() => {
    if (!user) {
      const channelA = supabase.channel('show');

      function handleURLChange(payload) {
        const { url } = payload;
        if (url && url !== window.location.href) {
          window.location.href = url;
        }
      }

      channelA.subscribe('urlchange', handleURLChange);

      return () => {
        channelA.unsubscribe();
      };
    }
  }, [user]);

  return (
    <>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/chat" element={<Chat />} />
        <Route path="/controls" element={<Controls />} />
        <Route path="/vote" element={<Vote />} />
        <Route path="/board" element={<Board />} />
      </Routes>
    </>
  );
}

export default App;
