import { useState, useEffect } from 'react';
import './App.css';
import Message from './Components/Message';
import Sender from './Components/Sender';


import { createClient } from '@supabase/supabase-js'
const supabaseUrl = "https://jftaxymlbutkjoacvtbk.supabase.co"
const supabaseKey = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImpmdGF4eW1sYnV0a2pvYWN2dGJrIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MTM5NjkxOTMsImV4cCI6MjAyOTU0NTE5M30.2aT1UzmiN34aKLNRUXAKwhKfnuwxMoiM3eMkHs1oyZU"
const supabase = createClient(supabaseUrl, supabaseKey)



function App() {
  const [username, setUsername] = useState('Anonymous');

  // Subscribing to messages

  useEffect(() => {
    // Define the handler function for inserts
    const handleInserts = (payload) => {
      console.log(payload);
      const message = payload.new.message;
      console.log(message);
    };

    // Create the channel and subscribe
    const channel = supabase
      .channel('custom-inserts')
      .on('postgres_changes', (payload) => {
        console.log('Change received!', payload);
        // Call the handler only if it's an insert event
        if (payload.event === 'INSERT') {
          handleInserts(payload);
        }
      });

    // Start listening to the channel
    channel.subscribe();

    // Clean up the subscription when the component unmounts
    return () => {
      channel.unsubscribe();
    };
  }, []); // Empty dependency array ensures this effect runs only once



  return (
    <>
      <h1 style={{
        color: 'grey',
        fontSize: '1.5rem',
        margin: '5px',
      }}>{username}</h1>
      <div className="Chat" style={{
        display: "flex",
        flexDirection: "column",
        alignItems: 'flex-start',
        justifyContent: 'flex-start',
      }}>
        <Message message="Hi there!" sender="Actor1" owner="true" />
        <Message message="Hey" sender="Actor2" owner="false" />
        <Message message="We all okay?" sender="Actor1" owner="true" />
        <Message message="How are ya" sender="Actor3" owner="false" />
        <Message message="I'm good" sender="Actor2" owner="false" />
        {/* The owner determines wether the messages were send by the user */}
      </div>
      <Sender username={username} />
    </>
  );
}

export default App;
