import React from 'react'

export default function Waiting() {


    const colors = [
        "blue",
        "aquamarine",
        "indigo",
        "mediumspringgreen",
        "purple"
    ];
    const spheres = [];
    for (let i = 0; i < 15; i++) {
        const color = colors[Math.floor(Math.random() * colors.length)];
        const x = Math.random() * window.innerWidth;
        const y = Math.random() * window.innerHeight;
        const zIndex = Math.random() * 10;
        spheres.push(
            <div key={i} style={{
                position: "absolute",
                backgroundColor: color,
                height: "200px",
                width: "200px",
                borderRadius: "50%",
                left: x + "px",
                top: y + "px",
                filter: "blur(200px)",
                WebkitBackfaceVisibility: "hidden", /* Fixes a bug in safari where the blur filter doesn't work */
                overflow: "hidden",
            }
            } />
        );
    }

    return (<>
        <div style={{
            height: "100vh",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            backgroundColor: "#1f1f1f",
        }}>
            <h1 style={{
                color: "white",
                fontSize: "50px",
                fontWeight: "bold",
            }}>Collecting :)...</h1>
        </div>
        <div style={{
            overflow: "hidden",
        }}>{spheres}</div>
    </>
    );
}
