import React, { useState, useEffect } from 'react';
import './App.css';
import Message from './Components/Message';
import Sender from './Components/Sender';
import { createClient } from '@supabase/supabase-js';

const supabaseUrl = "https://jftaxymlbutkjoacvtbk.supabase.co";
const supabaseKey = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImpmdGF4eW1sYnV0a2pvYWN2dGJrIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MTM5NjkxOTMsImV4cCI6MjAyOTU0NTE5M30.2aT1UzmiN34aKLNRUXAKwhKfnuwxMoiM3eMkHs1oyZU";
const supabase = createClient(supabaseUrl, supabaseKey);

function App() {
  const [username, setUsername] = useState('Anonymous');
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    // Subscribe to real-time inserts
    const subscription = supabase
      .channel('messages')
      .on('INSERT', payload => {
        const newMessage = {
          message: payload.new.message,
          sender: payload.new.sender,
          owner: true // Assuming the user owns all new messages
        };
        setMessages(prevMessages => [...prevMessages, newMessage]);
      })
      .subscribe();

    // Clean up the subscription when the component unmounts
    return () => subscription.unsubscribe();
  }, []);

  return (
    <>
      <h1 style={{
        color: 'grey',
        fontSize: '1.5rem',
        margin: '5px',
      }}>{username}</h1>
      <div className="Chat" style={{
        display: "flex",
        flexDirection: "column",
        alignItems: 'flex-start',
        justifyContent: 'flex-start',
      }}>
        {messages.map((msg, index) => (
          <Message
            key={index}
            message={msg.message}
            sender={msg.sender}
            owner={msg.owner}
          />
        ))}
      </div>
      <Sender username={username} />
    </>
  );
}

export default App;
