import { useEffect, useState } from "react";
import "../App.css";
import { Route, Routes } from 'react-router-dom';
import { createClient } from '@supabase/supabase-js';
import ActButton from "../Components/ActButton";
import ActGraph from "../Components/ActGraph";

const supabaseUrl = "https://jftaxymlbutkjoacvtbk.supabase.co";
const supabaseKey = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImpmdGF4eW1sYnV0a2pvYWN2dGJrIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MTM5NjkxOTMsImV4cCI6MjAyOTU0NTE5M30.2aT1UzmiN34aKLNRUXAKwhKfnuwxMoiM3eMkHs1oyZU";
const supabase = createClient(supabaseUrl, supabaseKey);

function Board(props) {
    const [acts, setActs] = useState([]);

    const [votes, setVotes] = useState([]);

    const [mostVoted, setMostVoted] = useState("None")

    // Join a room/topic. Can be anything except for 'realtime'.
    const channelA = supabase.channel('votes')

    // Simple function to log any messages we receive
    function messageReceived(payload) {
        // console.log(payload)
        setVotes([...votes, payload.payload.vote])
        console.log(`${votes.length} votes: `, votes)

        // Find which person was mosted voted
        const mostVoted = votes.reduce((prev, curr) => (prev.count || 0) > (curr.count || 0) ? prev : curr, {});
        


    }

    // Subscribe to the Channel
    channelA
        .on(
            'broadcast',
            { event: 'vote' },
            (payload) => messageReceived(payload)
        )
        .subscribe()


    useEffect(() => {
        async function fetchActs() {
            const { data, error } = await supabase.from('acts').select("*");
            if (error) {
                console.error("Error fetching acts:", error);
            } else {
                setActs(data);
            }
        }
        fetchActs();
    }, []);

    return (
        <>
            <h1>Board</h1>
            {acts.map((act, index) => (
                <ActGraph name={act.name} votes={0} />
            ))}

            <h1 id="mostvoted">Most voted: {mostVoted}</h1>
        </>
    );
}

export default Board;
