import React from "react";


import { createClient } from '@supabase/supabase-js'
const supabaseUrl = "https://jftaxymlbutkjoacvtbk.supabase.co"
const supabaseKey = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImpmdGF4eW1sYnV0a2pvYWN2dGJrIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MTM5NjkxOTMsImV4cCI6MjAyOTU0NTE5M30.2aT1UzmiN34aKLNRUXAKwhKfnuwxMoiM3eMkHs1oyZU"
const supabase = createClient(supabaseUrl, supabaseKey)



export default function Sender(props) {



    async function sendMessage() {
        const message = document.querySelector("input").value;
        const { data, error } = await supabase
            .from('messages')
            .insert([{ message: message, sender: props.username }])

        console.log(data, error)
    }
    return (
        <>
            <div style={{
                display: "flex",
                flexDirection: "row",
                flexWrap: "wrap",
                justifyContent: "center",
                width: "100%",
                alignItems: "flex-end",
                alignSelf: "flex-end",
                bottom: "10px",
                position: "fixed",
                marginTop: "10px",
                alignContent: "center",
            }}>
                <input  type="text" placeholder="Message" style={{
                    padding: "5px",
                    borderRadius: "5px",
                    border: "1px solid lightgrey",
                    width: "80%",
                    fontSize: "1rem",
                    outline: "none",
                }} />
                <button style={{
                    marginLeft: "5px",
                    padding: "5px",
                    backgroundColor: "#218aff",
                    color: "white",
                    border: "none",
                    borderRadius: "5px",
                    fontSize: "1rem",
                    fontWeight: "bold",
                }} onClick={sendMessage}>Send</button>
            </div>
        </>
    );
}
