import { useEffect, useState } from "react";
import "../App.css";
import Message from '../Components/Message';
import Sender from '../Components/Sender';
import { Route, Routes } from 'react-router-dom';



import { createClient } from '@supabase/supabase-js';
const supabaseUrl = "https://jftaxymlbutkjoacvtbk.supabase.co";
const supabaseKey = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImpmdGF4eW1sYnV0a2pvYWN2dGJrIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MTM5NjkxOTMsImV4cCI6MjAyOTU0NTE5M30.2aT1UzmiN34aKLNRUXAKwhKfnuwxMoiM3eMkHs1oyZU";
const supabase = createClient(supabaseUrl, supabaseKey);

function Running() {

    const sphereStyle = {
        position: "absolute",
        backgroundColor: "rgba(128, 128, 128, 0.2)",
        backdropFilter: "blur(10px)",
        borderRadius: "50%",
        animation: "spin 5s linear infinite",
        zIndex: -1,
    };
    const spheres = [
        {
            top: "10%",
            left: "10%",
            size: "20%",
            color: "rgba(128, 128, 128, 0.2)",
        },
        {
            top: "30%",
            left: "60%",
            size: "10%",
            color: "rgba(128, 192, 192, 0.2)",
        },
        {
            top: "70%",
            left: "40%",
            size: "15%",
            color: "rgba(128, 128, 255, 0.2)",
        },
    ];
    const styles = spheres.map((sphere, index) => ({
        ...sphereStyle,
        top: sphere.top,
        left: sphere.left,
        width: sphere.size,
        height: sphere.size,
        backgroundColor: sphere.color,
        key: index,
    }));
    const Spheres = () => styles.map((style) => <div style={style} />);

    return (
        <>
            <div style={{
                padding: "40px",
                alignItems: "center",
                justifyContent: "center",
                backgroundColor: "#1f1f1f",
                height: "100vh",
                color: "white",
            }}>
                <h1>You don't need your phone right now</h1>
            </div>
        </>
    );
}
export default Running;
