import './App.css';
import Message from './Components/Message';
import Sender from './Components/Sender';


import { createClient } from '@supabase/supabase-js'
const supabaseUrl = "https://jftaxymlbutkjoacvtbk.supabase.co"
const supabaseKey = "2"
const supabase = createClient(supabaseUrl, supabaseKey)

async function getUser() {
  const { user, error } = await supabase.auth.getUser()
  if (error) {
    console.error(error.message)
    return
  }

  if (!user) {
    console.log("User not found, signing in anonymously...")
    const { data, error } = await supabase.auth.signInAnonymously()
    if (error) {
      console.error(error.message)
      return
    }
    console.log("Signed in anonymously:", data)
  }
  console.log("User:", user)
}

getUser()


function App() {
  return (
    <>
      <div className="Chat" style={{
        display: "flex",
        flexDirection: "column",
        alignItems: 'flex-start',
        justifyContent: 'flex-start',
      }}>
        <Message message="Hi there!" sender="Actor1" owner="true" />
        <Message message="Hey" sender="Actor2" owner="false" />
        <Message message="We all okay?" sender="Actor1" owner="true" />
        <Message message="How are ya" sender="Actor3" owner="false" />
        <Message message="I'm good" sender="Actor2" owner="false" />
        {/* The owner determines wether the messages were send by the user */}
      </div>
      <Sender />
    </>
  );
}

export default App;
