import { useEffect, useState } from 'react';
import './App.css';
import Message from './Components/Message';
import Sender from './Components/Sender';

import { createClient } from '@supabase/supabase-js'
const supabaseUrl = "https://jftaxymlbutkjoacvtbk.supabase.co"
const supabaseKey = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImpmdGF4eW1sYnV0a2pvYWN2dGJrIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MTM5NjkxOTMsImV4cCI6MjAyOTU0NTE5M30.2aT1UzmiN34aKLNRUXAKwhKfnuwxMoiM3eMkHs1oyZU"
const supabase = createClient(supabaseUrl, supabaseKey)

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      const { user, error } = await supabase.auth.session();
      if (error) {
        console.error("Error fetching user:", error.message);
      } else {
        setUser(user);
      }
    };

    fetchUser();
  }, []);

  return (
    <>
      <div className="Chat" style={{
        display: "flex",
        flexDirection: "column",
        alignItems: 'flex-start',
        justifyContent: 'flex-start',
      }}>
        <Message message="Hi there!" sender="Actor1" owner={user ? "true" : "false"} />
        <Message message="Hey" sender="Actor2" owner={user ? "true" : "false"} />
        <Message message="We all okay?" sender="Actor1" owner={user ? "true" : "false"} />
        <Message message="How are ya" sender="Actor3" owner={user ? "true" : "false"} />
        <Message message="I'm good" sender="Actor2" owner={user ? "true" : "false"} />
        {/* The owner determines whether the messages were sent by the user */}
      </div>
      <Sender />
    </>
  );
}

export default App;
